import React, { Component } from "react";
import { Menu } from "semantic-ui-react";

class Pagination extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pageNumbers: [],
    };
  }

  componentDidMount() {
    this.setPageNumbers();
  }

  setPageNumbers() {
    let pageNumbers = [];
    console.log(this.props.totalRows, this.props.rowsPerPage);
    for (
      let i = 1;
      i <= Math.ceil(this.props.totalRows / this.props.rowsPerPage);
      i++
    ) {
      pageNumbers.push(i);
      console.log(i);
    }

    this.setState({ pageNumbers: this.state.pageNumbers });
    console.log(2, this.state.pageNumbers);
  }

  render() {
    const { paginate } = this.props;
    return (
      <Menu floated="right" pagination>
        {this.state.pageNumbers.map((number) => (
          <Menu.Item key={number} as="a" onClick={() => paginate(number)}>
            {number}
          </Menu.Item>
        ))}
      </Menu>
    );
  }
}

export default Pagination;
