import React, { Component } from "react";
import axios from "axios";
import { Button, Container, Table, Dropdown, Icon } from "semantic-ui-react";
import CustomerModal from "./CustomerModal";
import Pagination from "../Common/Pagination";

class Customer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      customers: [],
      customer: {},
      open: false,
      button: "",
      rowsPerPage: 10,
      totalRows: 0,
      curruntPage: 1,
      value: 10,
    };
  }

  componentDidMount() {
    this.fetchCustomers();
  }

  fetchCustomers = () => {
    axios
      .get("/Customers/GetCustomer")
      .then(({ data }) => {
        // console.log(data);
        this.setState({
          customers: data,
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  toggleCustomerModal = (value, button, customer) => {
    this.setState({
      open: value,
      button: button,
      customer: customer,
    });
  };

  render() {
    const { open, button, customer } = this.state;

    return (
      <Container>
        <CustomerModal
          open={open}
          button={button}
          customer={customer}
          fetchCustomers={this.fetchCustomers}
          toggleCustomerModal={this.toggleCustomerModal}
        />
        <Button
          color="blue"
          onClick={() => this.toggleCustomerModal(true, "Add", null)}
        >
          New Customer
        </Button>
        {/* Table */}
        <Table celled striped fixed>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Name</Table.HeaderCell>
              <Table.HeaderCell>Address</Table.HeaderCell>
              <Table.HeaderCell>Action</Table.HeaderCell>
              <Table.HeaderCell>Action</Table.HeaderCell>
            </Table.Row>
          </Table.Header>

          <Table.Body>
            {this.state.customers.map((customer) => (
              <Table.Row key={customer.id}>
                <Table.Cell>{customer.name}</Table.Cell>
                <Table.Cell>{customer.address}</Table.Cell>
                <Table.Cell>
                  <Button
                    onClick={() =>
                      this.toggleCustomerModal(true, "Edit", customer)
                    }
                    color="yellow"
                    icon
                    labelPosition="left"
                  >
                    <Icon name="edit" />
                    EDIT
                  </Button>
                </Table.Cell>
                <Table.Cell>
                  <Button
                    onClick={() => this.toggleCustomerModal(true, "", customer)}
                    color="red"
                    icon
                    labelPosition="left"
                  >
                    <Icon name="trash" />
                    DELETE
                  </Button>
                </Table.Cell>
              </Table.Row>
            ))}
          </Table.Body>
        </Table>
        {/* Pagination */}
        <Pagination totalRows={this.state.customers.length} rowsPerPage={10} />
        {/* drpdown selection */}
        <Dropdown compact selection options={options} />
      </Container>
    );
  }
}

const options = [
  { key: 10, text: "10", value: 10 },
  { key: 20, text: "20", value: 20 },
  { key: 30, text: "30", value: 30 },
];

export default Customer;
