import React from "react";
import { Table, Button, Icon } from "semantic-ui-react";
import CustomerModal from "./CustomerModal";

const CustomerTable = (props) => {
  const { customers } = props;

  const toggleCustomerModal = (value, button) => {
    this.setState({
      open: value,
      button: button,
    });
  };

  return (
    <div>
      <CustomerModal open={open} button={button} />
      <Table celled fixed>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>Name</Table.HeaderCell>
            <Table.HeaderCell>Address</Table.HeaderCell>
            <Table.HeaderCell>Actions</Table.HeaderCell>
            <Table.HeaderCell>Actions</Table.HeaderCell>
          </Table.Row>
        </Table.Header>
        <Table.Body>
          {customers.map((customer) => (
            <Table.Row key={customer.id}>
              <Table.Cell>{customer.name}</Table.Cell>
              <Table.Cell>{customer.address}</Table.Cell>
              <Table.Cell>
                <Button
                  onClick={() =>
                    toggleCustomerModal(
                      true,
                      "Edit",
                      customer.id,
                      customer.name,
                      customer.address
                    )
                  }
                  color="yellow"
                  icon
                  labelPosition="left"
                >
                  <Icon name="edit" />
                  EDIT
                </Button>
              </Table.Cell>
              <Table.Cell>
                <Button
                  onClick={() => toggleCustomerModal(true, customer.id)}
                  color="red"
                  icon
                  labelPosition="left"
                >
                  <Icon name="trash" />
                  DELETE
                </Button>
              </Table.Cell>
            </Table.Row>
          ))}
        </Table.Body>
      </Table>
    </div>
  );
};
export default CustomerTable;
