import React, { Component } from "react";
import axios from "axios";
import { Button, Modal, Form, Icon, Dropdown } from "semantic-ui-react";

class SalesModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      customers: [],
      customer: {},
      products: [],
      product: {},
      stores: [],
      store: {},
      DateSale: null,
    };
  }

  deleteSale = (id) => {
    axios
      .delete(`/Sales/DeleteSales/${id}`)
      .then((res) => {
        //console.log(res.data);
        this.props.fetchSales();
        this.props.toggleSalesModal(false);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  addSale = () => {
    //console.log(this.state.Name, this.state.Address);
    axios
      .post("/Sales/PostSales", {
        name: this.state.Name,
        address: this.state.Address,
      })
      .then((res) => {
        //console.log(res.data);
        this.props.fetchSales();
        this.props.toggleSalesModal(false);
        this.setState({ Address: "", Name: "" });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  editSale = (id) => {
    axios
      .put(`/Sales/PutSale/${id}`, {
        id: id,
        name: this.state.Name,
        address: this.state.Address,
      })
      .then((res) => {
        // console.log(res.data);
        this.props.fetchSales();
        this.props.toggleSalesModal(false);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  render() {
    const { open, button, sale, sales, toggleSalesModal } = this.props;

    if (button === "Add")
      return (
        <Modal centered={true} open={open}>
          <Modal.Header>Create Sale</Modal.Header>
          <Modal.Content>
            <Form>
              <Form.Field>
                <label>Date sold</label>
                <input
                  onChange={(e) => this.setState({ Name: e.target.value })}
                />
              </Form.Field>
              <Form.Field>
                <label>Customer</label>
                <Dropdown placeholder="Select Customer" fluid selection>
                  <Dropdown.Menu>
                    {sales.map((s) => (
                      <Dropdown.Item
                        key={s.customer.id}
                        text={s.customer.name}
                        value={s.customer.name}
                      />
                    ))}
                  </Dropdown.Menu>
                </Dropdown>
                {/* <input
                  placeholder="Select Customer"
                  onChange={(e) => this.setState({ Address: e.target.value })}
                /> */}
              </Form.Field>
              <Form.Field>
                <label>Product</label>
                <input
                  placeholder="Select Product"
                  onChange={(e) => this.setState({ Address: e.target.value })}
                />
              </Form.Field>
              <Form.Field>
                <label>Store</label>
                <input
                  placeholder="Select Store"
                  onChange={(e) => this.setState({ Address: e.target.value })}
                />
              </Form.Field>
            </Form>
          </Modal.Content>
          <Modal.Actions>
            <Button
              color="black"
              onClick={() => toggleSalesModal(false, "", "")}
            >
              Cancel
            </Button>
            <Button
              color="green"
              labelPosition="right"
              icon
              onClick={this.addSale}
            >
              <Icon name="checkmark" />
              Create
            </Button>
          </Modal.Actions>
        </Modal>
      );
    else if (button === "Edit")
      return (
        <Modal centered={true} open={open}>
          <Modal.Header>Edit Sale</Modal.Header>
          <Modal.Content>
            <Form>
              <Form.Field>
                <label>Name</label>
                <input
                  defaultValue={sale.name}
                  onChange={(e) => this.setState({ Name: e.target.value })}
                />
              </Form.Field>
              <Form.Field>
                <label>Address</label>
                <input
                  defaultValue={sale.address}
                  onChange={(e) => this.setState({ Address: e.target.value })}
                />
              </Form.Field>
            </Form>
          </Modal.Content>
          <Modal.Actions>
            <Button
              color="black"
              onClick={() => toggleSalesModal(false, "", "")}
            >
              Cancel
            </Button>
            <Button
              labelPosition="right"
              color="green"
              icon
              onClick={() => this.editSale(sale.id)}
            >
              <Icon name="checkmark" />
              Edit
            </Button>
          </Modal.Actions>
        </Modal>
      );
    else
      return (
        <Modal open={open}>
          <Modal.Header>Delete Sale</Modal.Header>
          <Modal.Content>Are you sure?</Modal.Content>
          <Modal.Actions>
            <Button
              color="black"
              onClick={() => toggleSalesModal(false, "", "")}
            >
              Cancel
            </Button>
            <Button
              labelPosition="right"
              icon
              onClick={() => this.deleteSale(sale.id)}
              positive
            >
              <Icon name="times" />
              Delete
            </Button>
          </Modal.Actions>
        </Modal>
      );
  }
}

export default SalesModal;
