import React, { Component } from "react";
import axios from "axios";
import { Button, Container, Dropdown } from "semantic-ui-react";
import CustomerTable from "./CustomerTable";
import CustomerModal from "./CustomerModal";

class Customer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      customers: [],
      open: false,
      rowsPerPage: 10,
      totalRows: 0,
      curruntPage: 1,
      customer: {},
    };
  }

  componentDidMount() {
    this.fetchCustomers();
  }

  // Records per the page
  fetchRecordsPerPage = (curruntPage, rowsPerPage) => {
    const indexOfLastRow = curruntPage * rowsPerPage;
    const indexOfFirstRow = indexOfLastRow - rowsPerPage;
    const customersPerPage = this.state.customers.slice(
      indexOfFirstRow,
      indexOfLastRow
    );
    this.setState({ customers: customersPerPage });
  };

  fetchCustomers = () => {
    axios
      .get("/Customers/GetCustomer")
      .then(({ data }) => {
        // console.log(data);
        this.setState({
          customers: data,
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  /* onChangeRowNumbers = (value) => {
    this.setState({
      rowsPerPage: value,
    });
    console.log(this.state.rowsPerPage, value);
  }; */

  toggleCustomerModal = (value, button) => {
    this.setState({
      open: value,
      button: button,
    });
  };

  render() {
    const { open, button } = this.state;
    return (
      <Container>
        <CustomerModal
          open={open}
          button={button}
          toggleCustomerModal={this.toggleCustomerModal}
          fetchCustomer={this.fetchCustomer}
        />
        <Button
          color="blue"
          onClick={() => this.toggleCustomerModal(true, "Add")}
        >
          New Customer
        </Button>
        <CustomerTable customers={this.state.customers} />
        <Dropdown
          options={[
            {
              key: "10",
              text: "10",
              value: 10,
            },
            {
              key: "20",
              text: "20",
              value: 20,
            },
            {
              key: "30",
              text: "30",
              value: 30,
            },
          ]}
          onChange={(e) => this.onChangeRowNumbers(e.target.value)}
          //onSelect={(value) => console.log("selected!", value)} // always fires once a selection happens even if there is no change
        />
      </Container>
    );
  }
}

export default Customer;
