import React, { Component } from "react";
import {
  Table,
  Button,
  Icon,
  Container,
  Dropdown,
  List,
} from "semantic-ui-react";

class CustomerTable extends Component {
  // Records per the page
  fetchRecordsPerPage = (curruntPage, rowsPerPage) => {
    const indexOfLastRow = curruntPage * rowsPerPage;
    const indexOfFirstRow = indexOfLastRow - rowsPerPage;
    const customersPerPage = this.state.customers.slice(
      indexOfFirstRow,
      indexOfLastRow
    );
    this.setState({ customers: customersPerPage });
  };

  render() {
    const { customers } = this.props;
    return (
      <Container>
        <List>
          {this.fetchRecordsPerPage(1, 10).map((customer) => (
            <List.Item key={customer.id}>{customer.name}</List.Item>
          ))}
        </List>
      </Container>
    );
  }
}

export default CustomerTable;
