import React, { Component } from "react";
import {
  Table,
  Button,
  Icon,
  Container,
  Menu,
  Dropdown,
} from "semantic-ui-react";
import axios from "axios";
import ProductModal from "./ProductModal";

class Product extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Products: [],
      open: false,
    };
  }
  componentDidMount() {
    this.fetchProduct();
  }

  fetchProduct = () => {
    axios
      .get("/Products/GetProduct")
      .then(({ data }) => {
        // console.log(data);
        this.setState({
          products: data,
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  toggleProductModal = (value, button, productId, productName) => {
    this.setState({
      open: value,
      button: button,
      productId: productId,
      productName: productName,
    });
  };

  paginationRecords = (numRecords) => {};
  fetchProductRecords = (numRecords) => {};

  options = [
    { key: 1, text: "10", value: 10 },
    { key: 2, text: "20", value: 20 },
    { key: 3, text: "50", value: 50 },
  ];

  render() {
    const { products, productId, open, button, productName, productAddress } =
      this.state;

    return (
      <Container>
        <ProductModal
          open={open}
          button={button}
          productId={productId}
          productName={productName}
          productAddress={productAddress}
          toggleProductModal={this.toggleProductModal}
          fetchProduct={this.fetchProduct}
        />
        <Button
          color="blue"
          onClick={() => this.toggleProductModal(true, "Add")}
        >
          New Product
        </Button>
        <Table celled fixed>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Name</Table.HeaderCell>
              <Table.HeaderCell>Address</Table.HeaderCell>
              <Table.HeaderCell>Actions</Table.HeaderCell>
              <Table.HeaderCell>Actions</Table.HeaderCell>
            </Table.Row>
          </Table.Header>
          <Table.Body>
            {products.map((product) => (
              <Table.Row key={product.id}>
                <Table.Cell>{product.name}</Table.Cell>
                <Table.Cell>{product.address}</Table.Cell>
                <Table.Cell>
                  <Button
                    onClick={() =>
                      this.toggleProductModal(
                        true,
                        "Edit",
                        product.id,
                        product.name,
                        product.address
                      )
                    }
                    color="yellow"
                    icon
                    labelPosition="left"
                  >
                    <Icon name="edit" />
                    EDIT
                  </Button>
                </Table.Cell>
                <Table.Cell>
                  <Button
                    onClick={() =>
                      this.toggleProductModal(true, null, Product.id)
                    }
                    color="red"
                    icon
                    labelPosition="left"
                  >
                    <Icon name="trash" />
                    DELETE
                  </Button>
                </Table.Cell>
              </Table.Row>
            ))}
          </Table.Body>
        </Table>

        {/* paganation section */}
        <Menu floated="right" pagination>
          <Menu.Item as="a">1</Menu.Item>
          <Menu.Item as="a">2</Menu.Item>
          <Menu.Item as="a">3</Menu.Item>
          <Menu.Item as="a">4</Menu.Item>
        </Menu>
        <div>
          <Menu floated="left" compact>
            <Dropdown options={this.options} simple item />
          </Menu>
        </div>
      </Container>
    );
  }
}

export default Product;
