import React, { Component } from "react";
import axios from "axios";
import { Button, Modal, Form, Icon } from "semantic-ui-react";

class SalesModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      customers: [],
      customer: {},
      products: [],
      product: {},
      stores: [],
      store: {},
      DateSale: null,
    };
  }

  deleteSale = (id) => {
    axios
      .delete(`/Sales/DeleteSales/${id}`)
      .then((res) => {
        //console.log(res.data);
        this.props.fetchSales();
        this.props.toggleSalesModal(false);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  addSale = () => {
    //console.log(this.state.Name, this.state.Address);
    axios
      .post("/Sales/PostSales", {
        name: this.state.Name,
        address: this.state.Address,
      })
      .then((res) => {
        //console.log(res.data);
        this.props.fetchSales();
        this.props.toggleSalesModal(false);
        this.setState({ Address: "", Name: "" });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  editSale = (id) => {
    axios
      .put(`/Sales/PutSale/${id}`, {
        id: id,
        name: this.state.Name,
        address: this.state.Address,
      })
      .then((res) => {
        // console.log(res.data);
        this.props.fetchSales();
        this.props.toggleSalesModal(false);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  render() {
    const { open, button, Sale, toggleSalesModal } = this.props;

    if (button === "Add")
      return (
        <Modal centered={true} open={open}>
          <Modal.Header>Create Sale</Modal.Header>
          <Modal.Content>
            <Form>
              <Form.Field>
                <label>Name</label>
                <input
                  placeholder="Sale Name"
                  onChange={(e) => this.setState({ Name: e.target.value })}
                />
              </Form.Field>
              <Form.Field>
                <label>Address</label>
                <input
                  placeholder="Sale Address"
                  onChange={(e) => this.setState({ Address: e.target.value })}
                />
              </Form.Field>
            </Form>
          </Modal.Content>
          <Modal.Actions>
            <Button
              color="black"
              onClick={() => toggleSalesModal(false, "", "")}
            >
              Cancel
            </Button>
            <Button
              disabled={this.state.Address.length < 5 ? true : false}
              color="green"
              labelPosition="right"
              icon
              onClick={this.addSale}
            >
              <Icon name="checkmark" />
              Create
            </Button>
          </Modal.Actions>
        </Modal>
      );
    else if (button === "Edit")
      return (
        <Modal centered={true} open={open}>
          <Modal.Header>Edit Sale</Modal.Header>
          <Modal.Content>
            <Form>
              <Form.Field>
                <label>Name</label>
                <input
                  defaultValue={Sale.name}
                  onChange={(e) => this.setState({ Name: e.target.value })}
                />
              </Form.Field>
              <Form.Field>
                <label>Address</label>
                <input
                  defaultValue={Sale.address}
                  onChange={(e) => this.setState({ Address: e.target.value })}
                />
              </Form.Field>
            </Form>
          </Modal.Content>
          <Modal.Actions>
            <Button
              color="black"
              onClick={() => toggleSalesModal(false, "", "")}
            >
              Cancel
            </Button>
            <Button
              labelPosition="right"
              color="green"
              icon
              onClick={() => this.editSale(Sale.id)}
            >
              <Icon name="checkmark" />
              Edit
            </Button>
          </Modal.Actions>
        </Modal>
      );
    else
      return (
        <Modal open={open}>
          <Modal.Header>Delete Sale</Modal.Header>
          <Modal.Content>Are you sure?</Modal.Content>
          <Modal.Actions>
            <Button
              color="black"
              onClick={() => toggleSalesModal(false, "", "")}
            >
              Cancel
            </Button>
            <Button
              labelPosition="right"
              icon
              onClick={() => this.deleteSale(Sale.id)}
              positive
            >
              <Icon name="times" />
              Delete
            </Button>
          </Modal.Actions>
        </Modal>
      );
  }
}

export default SalesModal;
