import React, { Component } from "react";
import { Table, Button, Icon } from "semantic-ui-react";
import axios from "axios";
import AddCustomer from "./AddCustomer";
import CustomerDelete from "./CustomerDelete";

class Customer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      customers: [],
      open: false,
    };
  }
  componentDidMount() {
    this.fetchCustomer();
  }

  fetchCustomer = () => {
    axios
      .get("/Customers/GetCustomer")
      .then(({ data }) => {
        // console.log(data);
        this.setState({
          customers: data,
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  toggleAddModal = (value) => {
    this.setState({
      open: value,
    });
  };

  render() {
    const { customers, open } = this.state;

    return (
      <div>
        <AddCustomer
          open={open}
          toggleAddModal={this.toggleAddModal}
          fetchCustomer={this.fetchCustomer}
        />
        <Button color="blue" onClick={() => this.toggleAddModal(true)}>
          New Customer
        </Button>
        <Table celled fixed>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Name</Table.HeaderCell>
              <Table.HeaderCell>Address</Table.HeaderCell>
              <Table.HeaderCell>Actions</Table.HeaderCell>
              <Table.HeaderCell>Actions</Table.HeaderCell>
            </Table.Row>
          </Table.Header>
          <Table.Body>
            {customers.map((customer) => (
              <Table.Row key={customer.id}>
                <Table.Cell>{customer.name}</Table.Cell>
                <Table.Cell>{customer.address}</Table.Cell>
                <Table.Cell>
                  <Button color="yellow" icon labelPosition="left">
                    <Icon name="edit" />
                    EDIT
                  </Button>
                </Table.Cell>
                <Table.Cell>
                  <CustomerDelete
                    open={open}
                    toggleAddModal={this.toggleAddModal}
                    fetchCustomer={this.fetchCustomer}
                    customerId={customer.id}
                  />
                  <Button
                    onClick={() => this.toggleAddModal(true)}
                    color="red"
                    labelPosition="left"
                  >
                    <Icon name="trash" />
                    DELETE
                  </Button>
                </Table.Cell>
              </Table.Row>
            ))}
          </Table.Body>
        </Table>
      </div>
    );
  }
}

export default Customer;
