import React, { Component } from "react";
import axios from "axios";
import { Button, Modal, Form, Icon } from "semantic-ui-react";

class CustomerModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Name: "",
      Address: "",
    };
  }
  render() {
    const { open, button, customers, customer } = this.props;
    if (button === "Add")
      return (
        <Modal centered={true} open={open}>
          <Modal.Header>Create Customer</Modal.Header>
          <Modal.Content>
            <Form>
              <Form.Field>
                <label>Name</label>
                <input
                  placeholder="Customer Name"
                  onChange={(e) => setName(e.target.value)}
                />
              </Form.Field>
              <Form.Field>
                <label>Address</label>
                <input
                  placeholder="Customer Address"
                  onChange={(e) => setAddress(e.target.value)}
                />
              </Form.Field>
            </Form>
          </Modal.Content>
          <Modal.Actions>
            <Button color="black" onClick={() => toggleCustomerModal(false)}>
              Cancel
            </Button>
            <Button
              disabled={Address.length === 0 ? true : false}
              color="green"
              labelPosition="right"
              icon
              onClick={addCustomer}
            >
              <Icon name="checkmark" />
              Create
            </Button>
          </Modal.Actions>
        </Modal>
      );
    else if (button === "Edit")
      return (
        <Modal centered={true} open={open}>
          <Modal.Header>Edit Customer</Modal.Header>
          <Modal.Content>
            <Form>
              <Form.Field>
                <label>Name</label>
                <input
                  defaultValue={customerName}
                  onChange={(e) => setName(e.target.value)}
                />
              </Form.Field>
              <Form.Field>
                <label>Address</label>
                <input
                  defaultValue={customerAddress}
                  onChange={(e) => setAddress(e.target.value)}
                />
              </Form.Field>
            </Form>
          </Modal.Content>
          <Modal.Actions>
            <Button color="black" onClick={() => toggleCustomerModal(false)}>
              Cancel
            </Button>
            <Button
              labelPosition="right"
              color="green"
              icon
              onClick={() => editCustomer(customerId)}
            >
              <Icon name="checkmark" />
              Edit
            </Button>
          </Modal.Actions>
        </Modal>
      );
    else
      return (
        <Modal open={open}>
          <Modal.Header>Delete Customer</Modal.Header>
          <Modal.Content>Are you sure?</Modal.Content>
          <Modal.Actions>
            <Button color="black" onClick={() => toggleCustomerModal(false)}>
              Cancel
            </Button>
            <Button
              labelPosition="right"
              icon
              onClick={() => deleteCustomer(customerId)}
              positive
            >
              <Icon name="times" />
              Delete
            </Button>
          </Modal.Actions>
        </Modal>
      );
  }
}

export default CustomerModal;
