import React, { Component } from "react";
import axios from "axios";
import moment from "moment";
import { Button, Modal, Form, Icon, Dropdown } from "semantic-ui-react";

class SalesModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      customer: {},
      product: {},
      store: {},
      dateSale: null,
      cusId: 0,
      proId: 0,
      stId: 0,
    };
  }

  deleteSale = (id) => {
    axios
      .delete(`/Sales/DeleteSales/${id}`)
      .then((res) => {
        //console.log(res.data);
        this.props.fetchSales();
        this.props.toggleSalesModal(false);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  addSale = () => {
    //console.log(this.state.Name, this.state.Address);
    axios
      .post("/Sales/PostSales", {
        productId: this.state.proId,
        customerId: this.state.cusId,
        storeId: this.state.stId,
        dateSold: this.state.dateSold,
      })
      .then((res) => {
        //console.log(res.data);
        this.props.fetchSales();
        this.props.toggleSalesModal(false);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  editSale = (id) => {
    axios
      .put(`/Sales/PutSale/${id}`, {
        id: id,
        productId: this.state.proId,
        customerId: this.state.cusId,
        storeId: this.state.stId,
        dateSold: this.state.dateSold,
      })
      .then((res) => {
        // console.log(res.data);
        this.props.fetchSales();
        this.props.toggleSalesModal(false);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  handleCusDropdown = (event, data) => {
    console.log(data.value);
    this.setState({
      cusId: data.value,
    });
  };

  handleProDropdown = (event, data) => {
    console.log(data.value);
    this.setState({
      proId: data.value,
    });
  };

  handleStoDropdown = (event, data) => {
    console.log(data.value);
    this.setState({
      stId: data.value,
    });
  };

  render() {
    const {
      open,
      button,
      sale,
      toggleSalesModal,
      customers,
      products,
      stores,
    } = this.props;

    if (button === "Add")
      return (
        <Modal centered={true} open={open}>
          <Modal.Header>Create Sale</Modal.Header>
          <Modal.Content>
            <Form>
              <Form.Field>
                <label>Date sold</label>
                <input
                  placeholder="MM/DD/YYYY"
                  defaultValue={moment().format("L")}
                  onChange={(e) => this.setState({ dateSold: e.target.value })}
                />
              </Form.Field>
              <Form.Field>
                <label>Customer</label>
                <Dropdown
                  placeholder="Select Customer"
                  fluid
                  selection
                  options={customers.map((c) => ({
                    key: c.id,
                    text: c.name,
                    value: c.id,
                  }))}
                  onChange={this.handleCusDropdown}
                />
              </Form.Field>
              <Form.Field>
                <label>Product</label>
                <Dropdown
                  placeholder="Select Product"
                  fluid
                  selection
                  options={products.map((p) => ({
                    key: p.id,
                    text: p.name,
                    value: p.id,
                  }))}
                  onChange={this.handleProDropdown}
                />
              </Form.Field>
              <Form.Field>
                <label>Store</label>
                <Dropdown
                  placeholder="Select Store"
                  fluid
                  selection
                  options={stores.map((s) => ({
                    key: s.id,
                    text: s.name,
                    value: s.id,
                  }))}
                  onChange={this.handleStoDropdown}
                />
              </Form.Field>
            </Form>
          </Modal.Content>
          <Modal.Actions>
            <Button
              color="black"
              onClick={() => toggleSalesModal(false, "", "")}
            >
              Cancel
            </Button>
            <Button
              color="green"
              labelPosition="right"
              icon
              onClick={this.addSale}
            >
              <Icon name="checkmark" />
              Create
            </Button>
          </Modal.Actions>
        </Modal>
      );
    else if (button === "Edit")
      return (
        <Modal centered={true} open={open}>
          <Modal.Header>Edit Sale</Modal.Header>
          <Modal.Content>
            <Form>
              <Form.Field>
                <label>Date sold</label>
                <input
                  placeholder="MM/DD/YYYY"
                  defaultValue={moment(sale.dateSold).format("L")}
                  onChange={(e) => this.setState({ dateSold: e.target.value })}
                />
              </Form.Field>
              <Form.Field>
                <label>Customer</label>
                <Dropdown
                  placeholder="Select Customer"
                  fluid
                  selection
                  defaultValue={sale.customer.id}
                  options={customers.map((c) => ({
                    key: c.id,
                    text: c.name,
                    value: c.id,
                  }))}
                  onChange={this.handleCusDropdown}
                />
              </Form.Field>
              <Form.Field>
                <label>Product</label>
                <Dropdown
                  placeholder="Select Product"
                  fluid
                  selection
                  defaultValue={sale.product.id}
                  options={products.map((p) => ({
                    key: p.id,
                    text: p.name,
                    value: p.id,
                  }))}
                  onChange={this.handleProDropdown}
                />
              </Form.Field>
              <Form.Field>
                <label>Store</label>
                <Dropdown
                  placeholder="Select Store"
                  fluid
                  selection
                  defaultValue={sale.store.id}
                  options={stores.map((s) => ({
                    key: s.id,
                    text: s.name,
                    value: s.id,
                  }))}
                  onChange={this.handleStoDropdown}
                />
              </Form.Field>
            </Form>
          </Modal.Content>
          <Modal.Actions>
            <Button
              color="black"
              onClick={() => toggleSalesModal(false, "", "")}
            >
              Cancel
            </Button>
            <Button
              labelPosition="right"
              color="green"
              icon
              onClick={() => this.editSale(sale.id)}
            >
              <Icon name="checkmark" />
              Edit
            </Button>
          </Modal.Actions>
        </Modal>
      );
    else
      return (
        <Modal open={open}>
          <Modal.Header>Delete Sale</Modal.Header>
          <Modal.Content>Are you sure?</Modal.Content>
          <Modal.Actions>
            <Button
              color="black"
              onClick={() => toggleSalesModal(false, "", "")}
            >
              Cancel
            </Button>
            <Button
              labelPosition="right"
              icon
              onClick={() => this.deleteSale(sale.id)}
              positive
            >
              <Icon name="times" />
              Delete
            </Button>
          </Modal.Actions>
        </Modal>
      );
  }
}

export default SalesModal;
