import React, { useState } from "react";
import { Button, Modal, Form, Icon } from "semantic-ui-react";
import axios from "axios";

const CustomerModal = (props) => {
  const [Name, setName] = useState("");
  const [Address, setAddress] = useState("");
  const [state, setState] = useState(false);

  const {
    open,
    button,
    customerId,
    customerName,
    customerAddress,
    toggleCustomerModal,
    fetchCustomer,
  } = props;

  const deleteCustomer = (id) => {
    axios
      .delete(`/Customers/DeleteCustomer/${id}`)
      .then((res) => {
        console.log(res.data);
        fetchCustomer();
        toggleCustomerModal(false);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const addCustomer = () => {
    axios
      .post("/Customers/PostCustomer", {
        name: Name,
        address: Address,
      })
      .then((res) => {
        console.log(res.data);
        fetchCustomer();
        toggleCustomerModal(false);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const editCustomer = (id) => {
    axios
      .put(`/Customers/PutCustomer/${id}`, {
        id: id,
        name: Name,
        address: Address,
      })
      .then((res) => {
        console.log(res.data);
        fetchCustomer();
        toggleCustomerModal(false);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleChange = (event) => {
    if (!setText(event.target.value)) {
      setSate(false);
    } else {
      setSate(true);
    }
  };

  if (button === "Add")
    return (
      <Modal centered={true} open={open}>
        <Modal.Header>Create Customer</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Field>
              <label>Name</label>
              <input
                placeholder="Customer Name"
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Field>
            <Form.Field>
              <label>Address</label>
              <input
                placeholder="Customer Address"
                onChange={(e) => setAddress(e.target.value)}
                onFocus={handleChange}
              />
            </Form.Field>
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button color="black" onClick={() => toggleCustomerModal(false)}>
            Cancel
          </Button>
          <Button disabled={state} color="green" icon onClick={addCustomer}>
            <Icon name="check" />
            Create
          </Button>
        </Modal.Actions>
      </Modal>
    );
  else if (button === "Edit")
    return (
      <Modal centered={true} open={open}>
        <Modal.Header>Edit Customer</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Field>
              <label>Name</label>
              <input
                placeholder={customerName}
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Field>
            <Form.Field>
              <label>Address</label>
              <input
                placeholder={customerAddress}
                onChange={(e) => setAddress(e.target.value)}
              />
            </Form.Field>
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button color="black" onClick={() => toggleCustomerModal(false)}>
            Cancel
          </Button>
          <Button color="green" icon onClick={() => editCustomer(customerId)}>
            <Icon name="check" />
            Edit
          </Button>
        </Modal.Actions>
      </Modal>
    );
  else
    return (
      <Modal open={open}>
        <Modal.Header>Delete Customer</Modal.Header>
        <Modal.Content>Are you sure?</Modal.Content>
        <Modal.Actions>
          <Button color="black" onClick={() => toggleCustomerModal(false)}>
            Cancel
          </Button>
          <Button
            labelPosition="right"
            icon
            onClick={() => deleteCustomer(customerId)}
            positive
          >
            <Icon name="times" />
            Delete
          </Button>
        </Modal.Actions>
      </Modal>
    );
};

export default CustomerModal;
