import React, { Component } from "react";
import { Table, Button, Icon, Dropdown } from "semantic-ui-react";

class CustomerTable extends Component {
  constructor() {
    super();
    this.state = {
      customers: [],
      open: false,
      rowsPerPage: 10,
      totalRows: 0,
      curruntPage: 1,
    };
  }
  toggleCustomerModal = (value, button, customer) => {
    this.setState({
      open: value,
      button: button,
      customer: customer,
    });
  };

  render() {
    const { customers } = this.props;
    return (
      <Table celled fixed>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>Name</Table.HeaderCell>
            <Table.HeaderCell>Address</Table.HeaderCell>
            <Table.HeaderCell>Actions</Table.HeaderCell>
            <Table.HeaderCell>Actions</Table.HeaderCell>
          </Table.Row>
        </Table.Header>
        <Table.Body>
          {customers.map((customer) => (
            <Table.Row key={customer.id}>
              <Table.Cell>{customer.name}</Table.Cell>
              <Table.Cell>{customer.address}</Table.Cell>
              <Table.Cell>
                <Button
                  onClick={() =>
                    this.toggleCustomerModal(true, "Edit", customer)
                  }
                  color="yellow"
                  icon
                  labelPosition="left"
                >
                  <Icon name="edit" />
                  EDIT
                </Button>
              </Table.Cell>
              <Table.Cell>
                <Button
                  onClick={() =>
                    this.toggleCustomerModal(true, "Delete", customer)
                  }
                  color="red"
                  icon
                  labelPosition="left"
                >
                  <Icon name="trash" />
                  DELETE
                </Button>
              </Table.Cell>
            </Table.Row>
          ))}
        </Table.Body>
      </Table>
    );
  }
}

export default CustomerTable;
