import React, { Component } from "react";
import axios from "axios";
import { Button, Container, Dropdown, Menu } from "semantic-ui-react";
import CustomerTable from "./CustomerTable";
import CustomerModal from "./CustomerModal";

class Customer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      customers: [],
      open: false,
      rowsPerPage: 10,
      totalRows: 0,
      curruntPage: 1,
      value: 10,
    };
  }

  componentDidMount() {
    this.fetchCustomers();
  }

  fetchCustomers = () => {
    axios
      .get("/Customers/GetCustomer")
      .then(({ data }) => {
        // console.log(data);
        this.setState({
          customers: data,
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  /* onChangeRowNumbers = (value) => {
    this.setState({
      rowsPerPage: value,
    });
    console.log(this.state.rowsPerPage, value);
  }; */

  toggleCustomerModal = (value, button) => {
    this.setState({
      open: value,
      button: button,
    });
  };

  handleChange = (event) => {
    this.setState({ value: event.target.value });
    console.log(this.value);
  };

  render() {
    return (
      <Container>
        <CustomerModal open={this.open} button={this.button} />
        <Button
          color="blue"
          onClick={() => this.toggleCustomerModal(true, "Add")}
        >
          New Customer
        </Button>
        <CustomerTable customers={this.state.customers} />
        {/* drpdown selection */}
      </Container>
    );
  }
}

export default Customer;
