import React, { Component } from "react";
import axios from "axios";
import { Button, Container, Dropdown, Menu } from "semantic-ui-react";
import CustomerTable from "./CustomerTable";
import CustomerModal from "./CustomerModal";

class Customer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      customers: [],
      open: false,
      rowsPerPage: 10,
      totalRows: 0,
      curruntPage: 1,
    };
  }

  componentDidMount() {
    this.fetchCustomers();
  }

  fetchCustomers = () => {
    axios
      .get("/Customers/GetCustomer")
      .then(({ data }) => {
        // console.log(data);
        this.setState({
          customers: data,
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  /* onChangeRowNumbers = (value) => {
    this.setState({
      rowsPerPage: value,
    });
    console.log(this.state.rowsPerPage, value);
  }; */

  toggleCustomerModal = (value, button) => {
    this.setState({
      open: value,
      button: button,
    });
  };

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  render() {
    const options = [
      { key: 1, text: "10", value: 1 },
      { key: 2, text: "20", value: 2 },
      { key: 3, text: "30", value: 3 },
    ];
    return (
      <Container>
        <CustomerModal open={this.open} button={this.button} />
        <Button
          color="blue"
          onClick={() => this.toggleCustomerModal(true, "Add")}
        >
          New Customer
        </Button>
        <CustomerTable customers={this.state.customers} />
        <Menu compact>
          <Dropdown
            options={options}
            simple
            item
            onChange={this.handleChange}
          />
        </Menu>
      </Container>
    );
  }
}

export default Customer;
