import React, { Component } from "react";
import { Table, Button, Icon, Container } from "semantic-ui-react";
import axios from "axios";
import CustomerModal from "./CustomerModal";
import CustomerTable from "./CustomerTable";

class Customer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      customers: [],
      open: false,
    };
  }
  componentDidMount() {
    this.fetchCustomer();
    this.toggleCustomerModal();
  }

  fetchCustomer = () => {
    axios
      .get("/Customers/GetCustomer")
      .then(({ data }) => {
        // console.log(data);
        this.setState({
          customers: data,
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  toggleCustomerModal = (value, button) => {
    this.setState({
      open: value,
      button: button,
    });
  };

  render() {
    const { open, button, customers } = this.state;

    return (
      <Container>
        <CustomerModal open={open} button={button} />
        <Button
          color="blue"
          onClick={() => this.toggleCustomerModal(true, "Add")}
        >
          New Customer
        </Button>
        <CustomerTable customers={customers} />
      </Container>
    );
  }
}

export default Customer;
