import React, { Component } from "react";
import axios from "axios";
import { Button, Container, Dropdown, Menu } from "semantic-ui-react";
import CustomerTable from "./CustomerTable";

class Customer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      customers: [],
      open: false,
      rowsPerPage: 10,
      totalRows: 0,
      customer: {},
    };
  }

  componentDidMount() {
    this.fetchCustomers();
  }

  // Records per the page
  fetchRecordsPerPage = (curruntPage, rowsPerPage) => {
    const indexOfLastRow = curruntPage * rowsPerPage;
    const indexOfFirstRow = indexOfLastRow - rowsPerPage;
    const customersPerPage = this.state.customers.slice(
      indexOfFirstRow,
      indexOfLastRow
    );
    this.setState({ customers: customersPerPage });
  };

  fetchCustomers = () => {
    axios
      .get("/Customers/GetCustomer")
      .then(({ data }) => {
        // console.log(data);
        this.setState({
          customers: data,
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  onChangeRowNumbers = (value) => {
    this.setState({
      rowsPerPage: value,
    });
    console.log(this.state.rowsPerPage, value);
  };

  render() {
    return (
      <Container>
        <CustomerTable customers={this.state.customers} />
        <Menu compact>
          <Dropdown
            floated="left"
            onChange={(e) => this.onChangeRowNumbers(e.target.value)}
          >
            <Dropdown.Item key="1" text="10" value="10" />
            <Dropdown.Item key="2" text="10" value="10" />
            <Dropdown.Item key="3" text="10" value="10" />
          </Dropdown>
        </Menu>
      </Container>
    );
  }
}

export default Customer;
